import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { login } from '../../services/auth';
import { setAuthToken, setAuthUser } from '../../utils/auth';
import '../../App.css';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [isWorker, setIsWorker] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const result = await login({ email, password });
      if (result?.token) {
        setAuthToken(result.token);
      }
      if (result?.user) {
        setAuthUser(result.user);
      }

      const role = result?.user?.role;
      if (role === 'admin') {
        navigate('/admin');
      } else if (role === 'provider') {
        navigate('/provider');
      } else if (role === 'customer') {
        navigate('/customer');
      } else {
        navigate('/services');
      }
    } catch (err) {
      setError(err.message || 'Login failed');
    }
  };

  return (
    <div className="login-page">
      
      {/* LEFT SIDE â€“ IMAGES */}
      <div className="login-visual">
        <div className="image-slider">
          <img src="https://images.unsplash.com/photo-1522202176988-66273c2fd55f" alt="slide-1" />
          <img src="https://images.unsplash.com/photo-1521737604893-d14cc237f11d" alt="slide-2" />
          <img src="https://images.unsplash.com/photo-1519389950473-47ba0277781c" alt="slide-3" />
        </div>
      </div>

      {/* RIGHT SIDE â€“ FORM */}
      <div className="login-form">
        {/* ðŸ”¥ THIS INNER DIV FIXES THE LAYOUT */}
        <div className="login-form-inner">

          <h2>Welcome Back</h2>

          {/* Toggle */}
          <div className="role-toggle">
            <span
              className={!isWorker ? 'active' : ''}
              onClick={() => setIsWorker(false)}
            >
              User
            </span>
            <span
              className={isWorker ? 'active' : ''}
              onClick={() => setIsWorker(true)}
            >
              Worker
            </span>
          </div>

          <form onSubmit={handleLogin}>
            <div className="input-group">
              <input
                type="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <label>Email Address</label>
            </div>

            <div className="input-group">
              <input
                type="password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <label>Password</label>
            </div>

            <button type="submit" className="btn-primary full-width" style={{backgroundColor:'var(--text-dark)',borderRadius:'20px'}}>
              Login as {isWorker ? 'Worker' : 'User'}
            </button>
            {error ? <p className="services-muted">{error}</p> : null}
          </form>

          <p className="signup-text">
            Donâ€™t have an account? <Link to="/signup">Sign Up</Link>
          </p>

        </div>
      </div>
    </div>
  );
};

export default Login;
